import Collapse from "@material-ui/core/Collapse";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemText from "@material-ui/core/ListItemText";
import ListSubheader from "@material-ui/core/ListSubheader";
import Paper from "@material-ui/core/Paper";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import { makeStyles } from "@material-ui/styles";
import Link from "next/link";
import PropTypes from "prop-types";
import React, { useState } from "react";
import { useRouter } from "next/router";

const useStyles = makeStyles(({ palette }) => ({
  root: {
    margin: "0 auto",
    paddingTop: 8,
    height: 64,
  },
  tabsRoot: {},
  tabsIndicator: {
    maxWidth: 56,
    transform: "translateX(52px)",
    backgroundColor: palette.grey[200],
  },
  tab: {
    color: palette.common.white,
    opacity: 1,
    fontSize: 16,
    fontWeight: 500,
    letterSpacing: "1px",
    minWidth: 160,
  },
  collapsed: {
    position: "absolute",
    left: 0,
    top: 64,
    width: "100%",
  },
  paper: {
    minHeight: 240,
  },
  subheader: {
    fontWeight: "bold",
  },
  cover: {
    display: "block",
    height: "100%",
    width: "100%",
    objectFit: "cover",
    padding: "16px 16px 16px 0",
  },
  anchor: {
    color: "inherit",
    textDecoration: "inherit",
  },
}));

const MegaMenu = ({ menus, subMenus, cover }) => {
  const [tabIndex, setTabIndex] = useState();
  const router = useRouter();
  const classes = useStyles();
  return (
    <div className={classes.root} onMouseLeave={() => setTabIndex(undefined)}>
      <Tabs centered classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }} value={tabIndex}>
        {menus.map((item, index) => (
          <Tab
            key={item.label}
            label={
              <Link href={item.href}>
                <a className={classes.anchor}>{item.label}</a>
              </Link>
            }
            className={classes.tab}
            onMouseOver={() => setTabIndex(index)}
            onFocus={() => setTabIndex(index)}
            onClick={e => {
              e.preventDefault();
              router.push(item.href);
            }}
          />
        ))}
      </Tabs>
      <Collapse in={tabIndex >= 0} className={classes.collapsed}>
        <Paper elevation={2} className={classes.paper}>
          <Container maxWidth={"md"}>
            <Grid container justify={"center"}>
              <Grid item xs={3}>
                <img src={cover} alt={"cover"} className={classes.cover} />
              </Grid>
              {subMenus.map(({ label, children }) => (
                <Grid key={label} item xs={3}>
                  <List
                    subheader={
                      <ListSubheader className={classes.subheader} disableSticky>
                        {label}
                      </ListSubheader>
                    }
                  >
                    {children.map(subLabel => (
                      <ListItem key={subLabel} button dense>
                        <ListItemText primary={subLabel} />
                      </ListItem>
                    ))}
                  </List>
                </Grid>
              ))}
            </Grid>
          </Container>
        </Paper>
      </Collapse>
    </div>
  );
};

MegaMenu.propTypes = {
  menus: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string.isRequired,
    }),
  ),
  subMenus: PropTypes.arrayOf(PropTypes.shape({})),
  cover: PropTypes.string,
};
MegaMenu.defaultProps = {
  menus: [],
  subMenus: [],
  cover:
    // eslint-disable-next-line max-len
    "https://images.unsplash.com/photo-1470468969717-61d5d54fd036?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=983&q=80",
};

export default MegaMenu;
